var ID = function () {
  // Math.random should be unique because of its seeding algorithm.
  // Convert it to base 36 (numbers + letters), and grab the first 9 characters
  // after the decimal.
  return Math.random().toString(16).substr(2, 7);
};

var hex = function () {
  // Math.random should be unique because of its seeding algorithm.
  // Convert it to base 36 (numbers + letters), and grab the first 9 characters
  // after the decimal.
  return Math.random().toString(16).substr(2, 4);
};

var random = function(items) {
    return items[Math.floor(Math.random() * items.length)];
}

function getRandomInt(min, max) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min + 1)) + min;
}

const manufacturers = flow.get('manufacturers')
const types = flow.get('sensors')

const networks = []

for(var i = 0;i < 100; i++) {
    const network = {
        id: ID(),
        driverVersion: '7.0.0',  
        applicationName:'zwavejs2mqtt',
        applicationVersion: '2.4.0',
        devices: [],
    }

    const total = getRandomInt(10, 50)
    for(let i = 1; i < total; i++) {
        network.devices.push({
            manufacturerId: random(manufacturers),
            productType: random(types),
            productId: '0x' + hex(),
            firmwareVersion: `${getRandomInt(1, 5)}.${getRandomInt(0, 255)}`
        });
    }

    networks.push(network)
}

node.send({payload: {data: networks}})
